class StageEnvironment:
    def __init__(self, config_path, topology_path):
        self.config = ConfigLoader.load(config_path)
        self.topology = TopologyLoader.load(topology_path)
        self.network = NetworkManager(self.topology)
        self.builder = BuildManager(self.config)

    def start(self, rebuild=False, clean=False):
        if rebuild or not self.builder.artifacts_exist():
            self.builder.build(clean=clean)
        self.network.start()
        self.network.wait_ready()

    def run_tests(self, test_dirs, filter_pattern=None):
        results = TestRunner.run(test_dirs, filter_pattern)
        return results
